import React from "react";
import './Card.styles.css'
import { useNavigate } from "react-router-dom";

function Card({ id , name, location, image }) {
  
  const navigate = useNavigate() 

  return (
    <div className="card">
      <div className="card__image">
        <img src={image} alt="" />
      </div>
      <div className="card__content">
        <h3 className="card__title" style={{color :"ButtonText"}}>{name.toUpperCase()}</h3>
        <p className="card__description" style={{color :"GrayText H"}}>{location.toUpperCase()}</p>
        <button className="card__read-more btn btn-primary" onClick={() => navigate(`/${id}`)}>
            Read More
        </button>
      </div>
    </div>
  );
}

export default Card;
