import React, { useState, useEffect } from 'react';
import './Home.styles.css';
import Card from '../Card/Card.component';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import LocationSelector from '../locationSelector/locationSelector.component';

const Home = ({allLocations}) => {
  const [clubs, setClubs] = useState(allLocations);
  const [selectedLocation , setSelectedLocation] = useState(null)

  useEffect(function() {
    if (selectedLocation) {
      const filteredClubs = allLocations.filter(club => { 
        return club.city === selectedLocation;
      });
      setClubs(filteredClubs);
    } else {
      setClubs(allLocations);
    }
  }, [selectedLocation]);

  function handleLocationSelect(selectedLocation) {
    setSelectedLocation(selectedLocation);
  }

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    initialSlide: 0,
    autoplay: true,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
          arrows: true,
          prevArrow: <div className="slick-prev">Previous</div>,
          nextArrow: <div className="slick-next">Next</div>,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
          arrows: true,
          prevArrow: <div className="slick-prev">Previous</div>,
          nextArrow: <div className="slick-next">Next</div>,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: true,
          prevArrow: <div className="slick-prev">Previous</div>,
          nextArrow: <div className="slick-next">Next</div>,
        },
      },
    ],
  };

  return (
    <div>
      <LocationSelector onLocationSelect={handleLocationSelect} />
      <Slider {...settings}>
      {
      clubs.map(function(item) {
        return (
          <div key={item._id}>
            <Card item={item} />
          </div>
        );
      })}
    </Slider>
    </div>
    
  );
}

export default Home;
