import Button from 'react-bootstrap/Button';
import React from "react";


const BookingBox = () =>{
    var InterestedCnt = 100 ; 
    var isAvailable = true ; 
    var Popularity = 0.99  ; 
    var EntryFees = 0 ; 
    var Organiser = "" 
    return(
        <div className="booking-container"  style = {{width : "100%" , height : "70%" }}>
            <div style= {{display : "flex" , justifyContent:"space-evenly" , alignContent:"center"}}>
                <p>Interested : {InterestedCnt}</p>
                {
                    isAvailable == true  ? <p>AVAILABLE</p> : <p>UNAVAILABLE</p>
                }
                <p>Popularity : {Popularity*100}% </p>
            </div>
            <div>
                <h2>Quick Look </h2>
                <p>Offers . Indoor Adventures . Food & Drinks . Sports</p>
                <p>Tuesday, 09 May , 07:00 PM</p>
                {
                    EntryFees ? <p>${EntryFees}</p> : <p>Free Entry</p>
                }
                <hr/>
                <p>Organised By : {Organiser}</p>
                <hr/>
                <p>Special Details : Guest list closes at 9:30 PM. Cover charges applicable at the venue post 9:30 PM for couples.</p>
            </div>
            <Button variant="danger" size="lg">
        Book
      </Button>
        </div>
    )
}

export default BookingBox ;
