import React from "react";
import moment from 'moment'

const DateView =(date)=>{
    
    var month = date.format('MMMM') 
    var day = date.foramt("dd")
    var year = date.format("YYYY")

    return( 
        <div>
            <p style= {{fontSize: "20px"}}>{month}</p>
            <p style= {{fontSize: "10px"}}>{day}</p> 
            <p style= {{fontSize: "10px"}}>{year}</p>
        </div>
    )
}

const EventDetail = (details) =>{
    var [date] = details 
    var moment_date = moment(date) 

    return(
        <div className="detail-container"  style={{display : 'flex'  , width:'100%' , height:'100px' , backgroundColor:'white'}} >
            <DateView date = {moment_date}/> 
        </div>
    )
}

export default EventDetail ;
