import React from "react";
import moment from 'moment'

const DateView =({moment_date})=>{
    
    var month = moment_date.format("MMMM") 
    var day = moment_date.format("dddd")
    var year = moment_date.format("YYYY")

    return( 
        <div style ={{ height : "100%" , width : "100" }}>
            <p style= {{fontSize: "20px" , color : "red"}}>{month}</p>
            <p style= {{fontSize: "10px" , color : "grey"}}>{day}</p> 
            <p style= {{fontSize: "10px" , color : "grey"}}>{year}</p>
        </div>
    )
}

const EventDetail = () =>{
    var details = "08-10-2001"
    var moment_date = moment(details) 

    return(
        <div className="detail-container"  style={{display : 'flex'  , width:'100%' , height:'100px' , backgroundColor:'white'}} >
            <div style = {{height : "100%" , width : "30%" , margin : "5px" }}>
                <DateView moment_date = {moment_date}/> 
            </div>
        </div>
    )
}

export default EventDetail ;
